package prc03_bakery;

public class BreadAndCharge {

	// 필드
	private int bread;   // 빵
	private int change;  // 잔돈
	
	
	public BreadAndCharge(int bread, int change) {
		super();
		this.bread = bread;
		this.change = change;
	}
	
	
	

	public BreadAndCharge() {
		super();
		// TODO Auto-generated constructor stub
	}




	public int getBread() {
		return bread;
	}

	public void setBread(int bread) {
		this.bread = bread;
	}

	public int getChange() {
		return change;
	}

	public void setChange(int change) {
		this.change = change;
	}
	
	
	
	
	

}
